
public class WhileLoop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		/*
		 * int i=1; while(i<=5) { System.out.println(i); i++; }
		 */
		/*
		 * int i=1,n=5; while(i<=n) { System.out.println(i); i++; }
		 */
		//reverse ke liye
		int i=10;
		while(i>=1) {
			System.out.println(i);
			i--;
		}
	}

}
