package com.numberpattern;

public class NumberPattern10 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//int f1=0,f2=1;
		for(int i=0;i<=6;i++)
		{
			for(int j=0;j<=i;j++)
			{
				
				System.out.print(j+" ");
			}
			System.out.println();
		}
	}

}
